import { Directive, TemplateRef } from '@angular/core';
/**
 * Used for rendering the content of the DateRange Popup.
 *
 * To define the cell template, nest an `<ng-template>` tag with the `kendoRangePopupTemplate` directive
 * inside the component tag.
 */
export class DateRangePopupTemplateDirective {
    constructor(templateRef) {
        this.templateRef = templateRef;
    }
}
DateRangePopupTemplateDirective.decorators = [
    { type: Directive, args: [{
                selector: '[kendoDateRangePopupTemplate]'
            },] },
];
/** @nocollapse */
DateRangePopupTemplateDirective.ctorParameters = () => [
    { type: TemplateRef, },
];
