import { Directive, ViewContainerRef } from '@angular/core';
import { DialogContainerService } from './dialog-container.service';
/**
 * Provides an insertion point for the Dialogs that are created through the Dialog service.
 *
 * @example
 * ```html-no-run
 * <div kendoDialogContainer></div>
 * ```
 *
 * Created Dialogs will be mounted after this element.
 *
 * For an example on sample usage, refer to the
 * [`DialogService.open`]({% slug api_dialog_dialogservice %}#toc-open) method.
 *
 */
export class DialogContainerDirective {
    constructor(container, service) {
        service.container = container;
    }
}
DialogContainerDirective.decorators = [
    { type: Directive, args: [{
                selector: '[kendoDialogContainer]'
            },] },
];
/** @nocollapse */
DialogContainerDirective.ctorParameters = () => [
    { type: ViewContainerRef, },
    { type: DialogContainerService, },
];
