import { Directive, ViewContainerRef } from '@angular/core';
import { WindowContainerService } from './window-container.service';
/**
 * Provides an insertion point for the Windows that are created through the Window service.
 *
 * @example
 * ```html-no-run
 * <div kendoWindowContainer></div>
 * ```
 *
 * Created Windows will be mounted after this element.
 *
 * For an example on sample usage, refer to the
 * [`WindowService.open`]({% slug api_dialog_windowservice %}#toc-open) method.
 */
export class WindowContainerDirective {
    constructor(container, service) {
        service.container = container;
    }
}
WindowContainerDirective.decorators = [
    { type: Directive, args: [{
                selector: '[kendoWindowContainer]'
            },] },
];
/** @nocollapse */
WindowContainerDirective.ctorParameters = () => [
    { type: ViewContainerRef, },
    { type: WindowContainerService, },
];
